import { createRouter, createWebHistory } from 'vue-router'
import Login from '../views/Login.vue' // 确保路径对
// 如果你有 Console 或 Terminal 组件，按需引入
// import TerminalPage from '../views/TerminalPage.vue' 

const routes = [
  {
    path: '/',
    name: 'Login',
    component: Login
  },
  {
    path: '/terminal', // 你的终端页面路由
    name: 'Terminal',
    // 路由懒加载写法（推荐）
    component: () => import('../views/TerminalPage.vue') 
  },
  // 404 路由
  {
    path: '/:pathMatch(.*)*',
    name: 'NotFound',
    redirect: '/'
  }
]

const router = createRouter({
  // Vue 3 使用 createWebHistory
  history: createWebHistory(process.env.BASE_URL),
  routes
})

export default router

import { createStore } from 'vuex' // 1. 修正了 Import 大写问题

// 2. 直接在这里定义，防止外部文件缺失导致报错
export default createStore({
  state: {
    // WebSSH 核心数据
    sshInfo: null,  // 存储当前连接信息
    sshList: null,  // 存储历史连接列表
    language: 'zh'  // 语言设置
  },
  getters: {
    // 构造连接字符串，Terminal.vue 会用到
  sshReq: state => {
  const s = state.sshInfo
  if (!s) return ''
  // 1. 拼接原始字符串
  const originStr = `${encodeURIComponent(s.username)}@${encodeURIComponent(s.hostname)}:${s.port}`
  
  // 2. 【核心修复】转为 Base64 发送给后端
  return window.btoa(originStr)
}

  },
  mutations: {
    // 设置 SSH 信息（TerminalPage.vue 会调用）
    SET_SSH(state, info) {
      state.sshInfo = info
      // 顺便存 Session 防止刷新丢失
      try {
        sessionStorage.setItem('sshInfo', JSON.stringify(info))
      } catch (e) {}
    },
    // 设置历史列表
    SET_LIST(state, list) {
      state.sshList = list
      try {
        localStorage.setItem('sshList', list)
      } catch (e) {}
    },
    // 设置语言
    SET_LANG(state, lang) {
      state.language = lang
    }
  },
  actions: {
    // 暂时不需要异步操作
  },
  modules: {
    // 如果有其他模块可以在这里引入
  }
})

import { createI18n } from 'vue-i18n'

// 定义语言包
const messages = {
  en: {
    message: {
